import React,{useState} from 'react';
import './App.css';
import Form from './Form';
import Weather from './Weather';

function App() {
  const [weather,setWeather] = useState([])
  const APIKEY = 'e76d36361a59d72f618a2450c26d53fb'
  // Set up Hooks in React with useState

  async function fetchData(e) {
    e.preventDefault()
    const apiData = await fetch(`https://api.openweathermap.org/data/2.5/weather?q=London,uk&APPID=${APIKEY}`)
    .then(res => res.json())
    .then(data => data)
    setWeather({
      data: apiData
    }
    )
  }
  //Asynchronous function to retrieve weather data from

  return (
    <div className="App">
      <h3>WEATHER APP</h3>
      <Form getWeather={fetchData} />
      {console.log(weather.data)}
    </div>
  );
}
//Hooks up fetch function to form then returns result
export default App;
